# Find all node_modules directories and check package.json
find / -type d -name "node_modules" 2>/dev/null | while read dir; do
 for pkg in "${packages[@]}"; do
   if [ -f "$dir/$pkg/package.json" ]; then
     version=$(grep version "$dir/$pkg/package.json" | cut -d":" -f2 | cut -d'"' -f2 | head -1)
     echo "$pkg $version ($dir/$pkg)"
   fi
   if [ -f "$dir/$pkg/package-lock.json" ]; then
     version=$(grep version "$dir/$pkg/package-lock.json" | cut -d":" -f2 | cut -d'"' -f2 | head -1)
     echo "$pkg $version ($dir/$pkg)"
   fi
 done
done | grep -E "$target_versions" | tee -a $log
echo "done" | tee -a $log
